import std.math;
import std.typecons;
/*
 Input to this function is a string represented multiple groups for nested parentheses separated by spaces.
For each of the group, output the deepest level of nesting of parentheses.
E.g. (()()) has maximum two levels of nesting while ((())) has three.
>>> parse_nested_parens("(()()) ((())) () ((())()())")
[2L, 3L, 1L, 3L]
*/
long[] parse_nested_parens(string paren_string) {
    std.vector<long> nesting_levels = std.vector<long>();
    std.vector<long> depths = std.vector<long>();
    std.vector<long> max_depth = std.vector<long>();
    for (int I = 0; I < paren_string.length; i++) {
        if (paren_string[i] == '(') {
            depths.push_back(depths.length + 1);
            max_depth.push_back(depths.length);
            nesting_levels.push_back(0);
        } else if (paren_string[i] == ')') {
            depths.push_back(depths.length - 1);
            max_depth.push_back(depths.length);
            nesting_levels.push_back(nesting_levels.back() + 1);
        } else if (paren_string[i] == '(') {
            depths.push_back(depths.back() + 1);
            max_depth.push_back(depths.back() + 1);
            nesting_levels.push_back(nesting_levels.back() + 1);
        } else if (paren_string[i] == ')') {
            depths.push_back(depths.back() - 1);
            max_depth.push_back(depths.back() - 1);
            nesting_levels.push_back(nesting_levels.back() - 1);
        }
    }
    return max_depth;
}

