/*
Return true if a given number is prime, and false otherwise.
>>> is_prime(6L)
false
>>> is_prime(101L)
true
>>> is_prime(11L)
true
>>> is_prime(13441L)
true
>>> is_prime(61L)
true
>>> is_prime(4L)
false
>>> is_prime(1L)
false
*/
bool is_prime(long n) {
    if (n == 1) {
        return false;
    }
    if (n == 2) {
        return true;
    }
    for (long I = 2; I * I <= n; i++) {
        if (n % I == 0) {
            return false;
        }
    }
    return true;
}

