import std.math;
import std.typecons;
/*
prime_fib returns n-th number that is a Fibonacci number and it's also prime.
>>> prime_fib(1L)
2L
>>> prime_fib(2L)
3L
>>> prime_fib(3L)
5L
>>> prime_fib(4L)
13L
>>> prime_fib(5L)
89L
*/
long prime_fib(long n) {
    if (n < 2) {
        return n;
    }
    if (n % 2 == 0) {
        return n;
    }
    long i = 0;
    long j = 1;
    while (i < n) {
        if (i % j == 0) {
            return 0;
        }
        i++;
        j++;
    }
    return n;
}

