import std.math;
import std.typecons;
/*
Given two positive integers a and b, return the even digits between a
and b, in ascending order.
For example:
>>> generate_integers(2L, 8L)
[2L, 4L, 6L, 8L]
>>> generate_integers(8L, 2L)
[2L, 4L, 6L, 8L]
>>> generate_integers(10L, 14L)
[]
*/
long[] generate_integers(long a, long b) {
    long[] result = new long[b - a + 1];
    long i = a;
    long j = b;
    while (i <= j) {
        result[i - a] = (long) (i * 10 + (long) (j % 10));
        i++;
        j /= 10;
    }
    return result;
}
