# For a given list of integers, return a tuple consisting of a sum and a product of all the integers in a list.
# Empty sum should be equal to 0 and empty product should be equal to 1.
# >>> HumanEval.sum_product([])
# {0, 1}
# >>> HumanEval.sum_product([1, 2, 3, 4])
# {10, 24}

defmodule HumanEval do
  def candidate(numbers), do: sum_product(numbers)
  def sum_product(numbers) do
    {sum(numbers), product(numbers)}
  end
  def sum(numbers), do: sum_product(numbers)
  def product(numbers), do: sum_product(numbers)
  def sum_product([], acc), do: acc
  def sum_product([x | xs], acc) do
    sum_product(xs, acc + x)
  end
end

# Given a positive integer n, return the count of the numbers of n-digit
# positive integers that start or end with 1.

defmodule HumanEval do
  def candidate(n), do: starts_one_ends(n)
  def starts_one_ends(n) do
    {n, n}
  end
  def starts_one_ends(n, acc) do
    {n, acc}
  end
  def starts_one_ends(n, acc, acc_end) do
    {n, acc_end}
  end
  def starts_one_ends(n, acc, acc_end, acc_start) do
    {n, acc_start}
  end
  def starts_one_ends(n, acc, acc_end, acc_start, acc_end_start) do
    {n, acc_end_start}
  end
  def starts_one_ends(n, acc, acc_end, acc_start, acc_end_start, acc_end_start_end) do
    {n, acc_end_start_end}
  end
  def starts_one_ends(n, acc, acc_end, acc_start, acc_end_start, acc_end_start_end, acc_end_start_end_start) do
    {n, acc_end_start_end_start}
  end
end

# Given a string representing a space separated lowercase letters, return a map
# of the letter with the most repetition and containing the corresponding count.
# If several letters have the same occurrence, return all of them.
# Example:
# >>> HumanEval.histogram("a b c")
# %{"a" => 1, "b" => 1, "c" => 1}
# >>> HumanEval.histogram("a b b a")
# %{"a" => 2, "b" => 2}
# >>> HumanEval.histogram("a b c a b")
# %{"a" => 2, "b" => 2}
# >>> HumanEval.histogram("b b b b a")
# %{"b" => 4}
# >>> HumanEval.histogram("")
# %{}

defmodule HumanEval do
  def candidate(test), do: histogram(test)
  def histogram(test) do
    map = test.split(" ").map(&String.downcase/1)
    count = map.reduce(0, fn(_, acc) -> acc + 1 end)
    {count, map}
  end
end
