// >>> compare([]int{0, 5, 0, 0, 0, 4}, []int{4, 1, 1, 0, 0, -2})
// []int{4, 4, 1, 0, 0, 6}
func compare(game []int, guess []int) []int {
    var result []int
    for i, g := range game {
        if g == guess[i] {
            result = append(result, 0)
        } else {
            result = append(result, int(g-guess[i]))
        }
    }
    return result
}
