func rolling_max(numbers: [Int]) -> [Int] {
    var max = numbers[0]
    var max_index = 0
    for I in 1...numbers.count {
        if numbers[i] > max {
            max = numbers[i]
            max_index = i
        }
    }
    return [max, max_index]

