/// >>> anti_shuffle(s: "hello")
/// "ehllo"
/// >>> anti_shuffle(s: "Hello World!!!")
/// "Hello !!!Wdlor"
func anti_shuffle(s: String) -> String {
    var result = s
    var index = 0
    var current = ""
    var next = ""
    for I in 0..<s.count {
        if s[i] == ' ' {
            if index == 0 {
                current = s[i + 1..i + 1]
                index = 0
            }
            next = s[i + 1..i + 1]
            result = result.replacingOccurrences(of: current, with: next)
            index = 0
        } else {
            if index == 0 {
                current = s[i + 1..i + 1]
                index = 0
            }
            next = s[i + 1..i + 1]
            result = result.replacingOccurrences(of: current, with: next)
            index = 0
        }
    }
    return result

