    let words = sentence.components(separatedBy: " ")
    var result = ""
    for word in words {
        if word.count == 1 {
            result += word
        } else {
            let prime = word.count % 2 == 0
            if prime {
                result += word
            } else {
                result += "(" + word + ")"
            }
        }
