/// [-1, -11, 1, -12, 11]
/// >>> order_by_points(nums: [] as [Int])
/// [] as [Int]
func order_by_points(nums: [Int]) -> [Int] {
    var sorted = [Int]()
    var sum = 0
    for num in nums {
        sum += num
    }
