/// [4, 4, 1, 0, 0, 6]
func compare(game: [Int], guess: [Int]) -> [Int] {
    var result = [Int]()
    var score = [Int]()
    for i in 0..<game.count {
        if game[i] == guess[i] {
            score[i] += 1
        } else {
            score[i] -= 1
        }
    }
    for i in 0..<score.count {
        result.append(score[i])
    }
    return result

