/// "#A@c"
func solve(s: String) -> String {
    var result = ""
    for I in 0..<s.count {
        if s[i] == 'a' {
            result += s[i..<i+1]
        } else {
            result += s[i..<i+1]
            result += s[i+1..<i+2]
        }
    }
    return result

